insert  into dfe_cclas_tributacao_ibs_cbs(id_cclas_ibs_cbs,id_cst_ibs_cbs,cst_ibs_cbs,descricao_cst_ibs_cbs,cclasstrib,nome_cclasstrib,descricao_cclasstrib,lc_redacao,lc_214_25,tipo_de_aliquota,predibs,predcbs,pred_ibs_cbs,ind_redutorbc,ind_gtribregular,ind_credpres,indmono,indmonoreten,indmonoret,indmonodif,credito_para,dinivig,dfimvig,dataatualizacao) values (132,17,'830','Exclusão de base de cálculo','830001','Documento com exclusão da BC da CBS e do IBS de energia elétrica fornecida pela distribuidora à UC','Documento com  exclusão da base de cálculo da CBS e do IBS refrente à energia elétrica fornecida pela distribuidora à unidade consumidora, conforme  Art 28, parágrafos 3° e 4°.','Art. 28. § 3º Exclui-se da base de cálculo da CBS e do IBS a energia elétrica fornecida pela distribuidora à unidade consumidora, na quantidade correspondente à energia injetada na rede de distribuição pela mesma unidade consumidora, acrescidos dos créditos de energia elétrica originados na própria unidade consumidora no mesmo mês, em meses anteriores ou em outra unidade consumidora do mesmo titular.\n§ 4º A exclusão de que trata o § 3º deste artigo:\nI - aplica-se somente a consumidores participantes do Sistema de Compensação de Energia Elétrica, de que trata a Lei nº 14.300, de 6 de janeiro de 2022;\nII - aplica-se somente à compensação de energia elétrica produzida por microgeração e minigeração, cuja potência instalada seja, respectivamente, menor ou igual a 75 kW e superior a 75 kW e menor ou igual a 1 MW; e\nIII - não se aplica ao custo de disponibilidade, à energia reativa, à demanda de potência, aos encargos de conexão ou uso do sistema de distribuição, aos componentes tarifárias não associadas ao custo da energia e a quaisquer outros valores cobrados pela distribuidora.','Art 28, parágrafos 3° e 4°','Sem alíquota',0.0000,0.0000,0.0000,'NAO','NAO','NAO','NAO','NAO','NAO','NAO',NULL,NULL,NULL,'2025-06-11 00:00:00');
